- (void)callbackForFetch:(PFObject *)result error:(NSError *)error {
    if (result) {
        NSLog(@"Success!");
    } else {
        if ([error code] == kPFErrorObjectNotFound) {
            NSLog(@"Object not available!");
        } else if (error) {
            NSLog(@"Error: %@", [[error userInfo] objectForKey:@"error"]);
        }
    }
}
