private function loadPreferences():void {
file = File.documentsDirectory.resolvePath("prefs.txt");
stream = new FileStream();
stream.open(file, FileMode.READ);
var prefs:String = stream.readUTFBytes(stream.bytesAvailable);
stream.close();
}

